# -*- coding: utf-8 -*-from __future__ import unicode_literalsfrom django.db import connectionsimport jsonimport osimport operatordef initAssyData(model_name):    result = []    try:        cur = connections[model_name].cursor()        cur.execute("SELECT process_id,assy_text,process_cd,process_text FROM m_assy ORDER BY process_id")        rows = cur.fetchall()        for row in rows:            result.append({'process_id':row[0],'process_cd':row[2],'process_text':row[3],'assy_text':row[1]})    except BaseException as exp:        print(exp)    connections[model_name].close()    return resultdef update_assy(model_name,process_id,assy_text):    try:        cur = connections[model_name].cursor()        SQL = 'UPDATE m_assy SET assy_text = %s WHERE process_id = %s'        cur.execute(SQL,(assy_text,process_id,))        connections[model_name].commit        cur.close()        result = {'status': 'success'}    except BaseException as exp:        print(exp)        result = {'status': 'fail'}    connections[model_name].close()    return result